import React, { useContext } from 'react';
import StarWarsContext from '../Context/StarWarsContext';
import Filters from './Filters';

export default () => {
  const { filters, data } = useContext(StarWarsContext);

  const renderHeader = () => (
    <tr>
      <th>Name</th>
      <th>Rotation Period</th>
      <th>Orbital Period</th>
      <th>diameter</th>
      <th>Climate</th>
      <th>Gravity</th>
      <th>Terrain</th>
      <th>Surface Water</th>
      <th>Population</th>
      <th>Films</th>
      <th>Created</th>
      <th>Edited</th>
      <th>URL</th>
    </tr>
  );

  const renderBody = (list) => (
    list.map((planet, index) => (
      <tr key={ index }>
        <td data-testid="planet-name">{ planet.name }</td>
        <td>{ Number(planet.rotation_period) }</td>
        <td>{ Number(planet.orbital_period) }</td>
        <td>{ Number(planet.diameter) }</td>
        <td>{ planet.climate }</td>
        <td>{ planet.gravity }</td>
        <td>{ planet.terrain }</td>
        <td>{ Number(planet.surface_water) }</td>
        <td>{ planet.population }</td>
        <td>{ planet.films }</td>
        <td>{ planet.created }</td>
        <td>{ planet.edited }</td>
        <td>{ planet.url }</td>
      </tr>
    ))
  );

  const isNumber = (value) => Number.isNaN(+value) ? value : +value;

  const orderTable = (list) => {
    const { column, sort } = filters.order;
    const one = 1;
    const oneLess = -1;

    if (isNumber(column)) {
      console.log(column);
      list.sort((a, b) => a - b);
    }

    if (sort === 'ASC') {
      list.sort((a, b) => {
        if (a[column] > b[column]) return one;
        if (b[column] > a[column]) return oneLess;
        return 0;
      });
    }
    if (sort === 'DESC') {
      list.sort((a, b) => {
        if (a[column] < b[column]) return one;
        if (b[column] < a[column]) return oneLess;
        return 0;
      });
    }
  };

  const filterPlanets = () => {
    let planets = data
      .filter((planet) => planet.name.match(filters.filterByName.name));

    if (filters.filterByNumericValues.length) {
      filters.filterByNumericValues
        .forEach(({ column, comparison, value }) => {
          if (comparison === 'maior que') {
            planets = planets
              .filter((planet) => Number(planet[column]) > value);
          }
          if (comparison === 'menor que') {
            planets = planets
              .filter((planet) => Number(planet[column]) < value);
          }
          if (comparison === 'igual a') {
            planets = planets
              .filter((planet) => Number(planet[column]) === Number(value));
          }
        });
    }
    orderTable(planets);
    return planets;
  };

  return (
    <>
      <Filters />
      <table>
        <tbody>
          { renderHeader() }
          { renderBody(filterPlanets()) }
        </tbody>
      </table>
    </>
  );
};
