import React, { useContext } from 'react';
import StarWarsContext from '../Context/StarWarsContext';

const FiltersComponent = () => {
  const { setFilterName, setFilterColumn } = useContext(StarWarsContext);

  const getvalueInput = ({ target }) => {
    const { value } = target;
    setFilterName(value);
  };

  const renderFilters = () => (
    // O primeiro deve abrir um dropdown que permita a quem usa selecionar uma das seguintes colunas: population, orbital_period, diameter, rotation_period e surface_water.Deve ser uma tag select com a propriedade data - testid='column-filter';
    // O segundo deve determinar se a faixa de valor será maior que, menor que ou igual a o numero que virá a seguir.Uma tag select com a propriedade data - testid='comparison-filter';
    // O terceiro deve ser uma caixa de texto que só aceita números.Essa caixa deve ser uma tag input com a propriedade data - testid='value-filter';
    <form>
      <label htmlFor="ask">
        Planeta:
        <input
          type="text"
          name="ask"
          id="ask"
          data-testid="name-filter"
          placeholder="Digite o planeta"
          onChange={ (e) => getvalueInput(e) }
        />
      </label>
      <label htmlFor="colums">
        Colunas:
        <select id="colums" data-testid="column-filter">
          <option value="population">population</option>
          <option value="orbital_period">orbital_period</option>
          <option value="diameter">diameter</option>
          <option value="rotation_period">rotation_period</option>
          <option value="surface_water">surface_water</option>
        </select>
      </label>
      <label htmlFor="comparison">
        Comparação
        <select id="comparison" data-testid="comparison-filter">
          <option value="maior que">maior que</option>
          <option value="menor que">menor que</option>
          <option value="igual a">igual a</option>
        </select>
        <input
          type="number"
          name="value-filter"
          id="value-filter"
          data-testid="value-filter"
          placeholder="Digite um numero"
        />
        <button type="button" >Filtrar</button>
      </label>
    </form>
  );

  return (
    renderFilters()
  );
};

export default FiltersComponent;
