import React, { useContext, useEffect } from 'react';
import StarWarsContext from '../Context/StarWarsContext';
import FiltersComponent from './Filters';

const Table = () => {
  const {
    data,
    filters,
    planets,
    setPlanets,
  } = useContext(StarWarsContext);

  useEffect(() => {
    setPlanets(planets);
  }, [planets]);

  const renderHeader = () => (
    <tr>
      <th>Name</th>
      <th>Rotation Period</th>
      <th>Orbital Period</th>
      <th>diameter</th>
      <th>Climate</th>
      <th>Gravity</th>
      <th>Terrain</th>
      <th>Surface Water</th>
      <th>Population</th>
      <th>Films</th>
      <th>Created</th>
      <th>Edited</th>
      <th>URL</th>
    </tr>
  );

  const renderBody = (list) => (
    list.map((planet, index) => (
      <tr key={ index }>
        <td data-testid="planet-name">{ planet.name }</td>
        <td>{ planet.rotation_period }</td>
        <td>{ planet.orbital_period }</td>
        <td>{ planet.diameter }</td>
        <td>{ planet.climate }</td>
        <td>{ planet.gravity }</td>
        <td>{ planet.terrain }</td>
        <td>{ planet.surface_water }</td>
        <td>{ planet.population }</td>
        <td>{ planet.films }</td>
        <td>{ planet.created }</td>
        <td>{ planet.edited }</td>
        <td>{ planet.url }</td>
      </tr>
    ))
  );

  const filterByName = () => {
    const { filterByName: { name } } = filters;
    if (name) return data.filter((planet) => planet.name.match(name) && planet.name);
    return data;
  };

  const filtersByNumber = (listPlanets) => {
    const { value, column, comparison } = filters.filterByNumericValues[0];
    setPlanets(listPlanets.filter((planet) => {
      if (comparison === 'igual a') {
        return Number(planet[column]) === Number(value);
      }
      if (comparison === 'menor que') {
        return Number(planet[column]) < Number(value);
      }
      return Number(planet[column]) > Number(value);
    }));
  };

  const handleClickFilter = () => {
    filtersByNumber(filterByName());
  };

  if (planets.lenght) {
    return (
      <>
        <FiltersComponent
          handleClickFilter={ handleClickFilter }
        />
        <table>
          <tbody>
            { renderHeader() }
            { renderBody(planets) }
          </tbody>
        </table>
      </>
    );
  }
  return (
    <>
      <FiltersComponent
        handleClickFilter={ handleClickFilter }
      />
      <table>
        <tbody>
          { renderHeader() }
          { renderBody(filterByName()) }
        </tbody>
      </table>
    </>
  );
};

export default Table;
