import { shape } from 'prop-types';
import React, { createContext, useContext, useState } from 'react';
import getPlanetsStarWars from '../Services/fetchAPI';

const StarWarsContext = createContext();

export const StarWarsProvider = ({ children }) => {
  const [test, setTest] = useState('Hello, app');

  const fetchPlanetsStarWars = async () => {
    const {
      name,
      rotation_period,
      orbital_period,
      diameter,
      climate,
      gravity,
      terrain,
      surface_water, population, residents, films, created, edited, url } = await getPlanetsStarWars();
  };

  const context = { fetchPlanetsStarWars, test };
  return (
    <StarWarsContext.Provider value={ context }>
      {children}
    </StarWarsContext.Provider>
  );
};

StarWarsProvider.propTypes = {
  children: shape().isRequired,
};

export const useStarWarsContext = () => useContext(StarWarsContext);
