import { shape } from 'prop-types';
import React, { useEffect, useState } from 'react';
import getPlanetsStarWars from '../Services/fetchAPI';
import StarWarsContext from './StarWarsContext';

const StarWarsProvider = ({ children }) => {
  const [data, setData] = useState([]);
  const [planets, setPlanets] = useState([]);
  const [filters, setFilters] = useState({
    filterByName: {
      name: '',
    },
    filterByNumericValues: [],
    order: {
      column: 'Name',
      sort: 'ASC',
    },
  });

  const fetchPlanetsStarWars = async () => {
    const { results } = await getPlanetsStarWars();
    results.forEach((element) => delete element.residents);
    setData(results);
  };

  useEffect(() => {
    fetchPlanetsStarWars();
  }, []);

  useEffect(() => {
    setPlanets(data);
  }, [data]);

  const filterByName = () => data
    .filter((planet) => planet.name.match(filters.filterByName.name));

  const filtersByNumber = (planetsFiltered) => {
    (planetsFiltered
      .filter((planet) => Object.entries(planet)
        .filter((values) => console.log(filters
          .filterByNumericValues.concat(values[1])))));
  };

  useEffect(() => {
    filtersByNumber(filterByName());
  }, [filters]);

  const context = {
    data,
    setFilters,
    filters,
    planets,
    setPlanets,
  };

  return (
    <StarWarsContext.Provider value={ context }>
      {children }
    </StarWarsContext.Provider>
  );
};

StarWarsProvider.propTypes = {
  children: shape().isRequired,
};

export default StarWarsProvider;
