import { shape } from 'prop-types';
import React, { useEffect, useState } from 'react';
import getPlanetsStarWars from '../Services/fetchAPI';
import StarWarsContext from './StarWarsContext';

const StarWarsProvider = ({ children }) => {
  const [data, setData] = useState([]);
  const [planets, setPlanets] = useState([]);
  const [filters, setFilters] = useState({
    filterByName: {},
    filterByNumericValues: [{}],
    order: {},
  });

  const fetchPlanetsStarWars = async () => {
    const { results } = await getPlanetsStarWars();
    results.forEach((element) => delete element.residents);
    setData(results);
  };

  useEffect(() => {
    fetchPlanetsStarWars();
    setFilters(data);
  }, []);

  const filterByNames = (name) => {
    setFilters(data.filter((planet) => planet.name.match(name)));
  };

  const filtersByNumber = (planetsFiltered) => {
    const { column, comparison, value } = filters.filterByNumericValues;
    if (comparison === 'igual a') {
      setFilters(planetsFiltered
        .filter((planet) => Number(planet[column]) === Number(value)));
    }
    if (comparison === 'menor que') {
      setFilters(planetsFiltered
        .filter((planet) => Number(planet[column]) < Number(value)));
    }
    if (comparison === 'maior que') {
      setFilters(planetsFiltered
        .filter((planet) => Number(planet[column]) > Number(value)));
    }
    return true;
  };

  const handleClickFilter = (name) => {
    setPlanets(filtersByNumber(filterByNames(name)));
  };

  const context = {
    data,
    setFilters,
    filters,
    planets,
    setPlanets,
    filterByName,
    filtersByNumber,
    handleClickFilter,
  };

  return (
    <StarWarsContext.Provider value={ context }>
      {children }
    </StarWarsContext.Provider>
  );
};

StarWarsProvider.propTypes = {
  children: shape().isRequired,
};

export default StarWarsProvider;
