import React, { useContext } from 'react';
import StarWarsContext from '../Context/StarWarsContext';
import FiltersComponent from './Filters';

const Table = () => {
  const { planets, setPlanets, filters } = useContext(StarWarsContext);

  const renderHeader = () => (
    <tr>
      <th>Name</th>
      <th>Rotation Period</th>
      <th>Orbital Period</th>
      <th>diameter</th>
      <th>Climate</th>
      <th>Gravity</th>
      <th>Terrain</th>
      <th>Surface Water</th>
      <th>Population</th>
      <th>Films</th>
      <th>Created</th>
      <th>Edited</th>
      <th>URL</th>
    </tr>
  );

  const renderBody = (list) => (
    list.map((planet, index) => (
      <tr key={ index }>
        <td data-testid="planet-name">{ planet.name }</td>
        <td>{ planet.rotation_period }</td>
        <td>{ planet.orbital_period }</td>
        <td>{ planet.diameter }</td>
        <td>{ planet.climate }</td>
        <td>{ planet.gravity }</td>
        <td>{ planet.terrain }</td>
        <td>{ planet.surface_water }</td>
        <td>{ planet.population }</td>
        <td>{ planet.films }</td>
        <td>{ planet.created }</td>
        <td>{ planet.edited }</td>
        <td>{ planet.url }</td>
      </tr>
    ))
  );

  const filterPlanets = filters.filterByNumericValues
    .forEach(({ column, comparison, value }) => {
      console.log(planets
        .filter((planet) => planet[column]));
    });

  console.log(filterPlanets);

  return (
    <>
      <FiltersComponent />
      <table>
        <tbody>
          { renderHeader() }
          { renderBody(planets) }
        </tbody>
      </table>
    </>
  );
};

export default Table;
